@boundClass
class AnterosLoginContainer2 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      userName: '',
      password: '',
      owner: undefined,
      remindMe: null,
      error: undefined
    };
    this.login = this.login.bind(this);
  }

  login() {
    let _this = this;
    _this.setState({ ..._this.state, error: undefined });
    this.props.dispatch(
      actions.login(
        {
          username: this.state.userName,
          password: this.state.password,
          owner: this.state.owner
        },
        (user, error) => {
          if (error) {
            _this.setState({ ..._this.state, error: error });
          } else {
            _this.props.onLoginSuccess(user);
          }
        }
      )
    );
    if (typeof Storage !== 'undefined') {
      if (this.state.remindMe) {
        localStorage.setItem('owner', this.state.owner);
        localStorage.setItem('remindMe', this.state.remindMe);
      } else {
        localStorage.removeItem('owner', this.state.owner);
        localStorage.removeItem('remindMe', this.state.remindMe);
      }
    }
  }

  onChangeOwner(event, value) {
    this.setState({
      ...this.state,
      owner: value
    });
  }

  onChangeUser(event, value) {
    this.setState({
      ...this.state,
      userName: value
    });
  }

  onChangePassword(event, value) {
    this.setState({
      ...this.state,
      password: event.target.value
    });
  }

  onChangeRemindMe(value, checked, checkbox) {
    this.setState({
      ...this.state,
      remindMe: checked
    });
  }

  componentWillMount() {
    if (this.state.remindMe === null) {
      if (localStorage.hasOwnProperty('remindMe')) {
        this.setState({
          ...this.state,
          remindMe: JSON.parse(localStorage.getItem('remindMe')),
          owner: localStorage.getItem('owner')
        });
      } else {
        this.setState({ ...this.state, remindMe: false });
      }
    }
  }

  render() {
    const { loading } = this.state;
    const { authenticating } = this.props;
    return (
      <AnterosFullBackground
        color="#4F9BD4"
        custom="#4F9BD4"
        style={{
          overflowY: 'hidden',
          display: 'flex'
        }}
      >
        {loading && <AnterosLinearProgressBar />}
        <div
          style={{
            ...imgHolder,
            height: '100%'
          }}
        >
          <div style={infoHolder}>
            <AnterosImage
              width="300px"
              src={require('../assets/img/AnterosTecnologia-blue.png')}
              margin="0 0 60px 0"
            />
            <h3>Administra√ß√£o de sistemas, recursos, a√ß√µes e usu√°rios.</h3>
            <p>
              Mantenha o total controle sobre o que os seus usu√°rios acessam e veja
              o que andam fazendo.
            </p>
            <AnterosImage
              width="100%"
              maxWidth="378px"
              src={require('../assets/img/abertura3.png')}
              alt=""
            />
          </div>
        </div>
        <div style={formHolder}>
          <AnterosForm style={form}>
            <AnterosLabel
              caption="Informe suas credencias para acesso:"
              small={12}
              style={{
                fontSize: '22px',
                color: '#464D69'
              }}
            />
            <AnterosFormGroup row={false}>
              <AnterosLabel
                caption="Identificador propriet√°rio"
                small={6}
                style={{
                  color: '#464D69'
                }}
              />
              <AnterosEdit
                small={12}
                id="empresa"
                onChange={this.onChangeOwner}
                value={this.state.owner}
                disabled={authenticating}
                icon="fal fa-building"
              />
            </AnterosFormGroup>
            <AnterosFormGroup row={false}>
              <AnterosLabel
                caption="Usu√°rio"
                small={2}
                style={{
                  color: '#464D69'
                }}
              />
              <AnterosEdit
                small={12}
                id="userName"
                onChange={this.onChangeUser}
                value={this.state.userName}
                disabled={authenticating}
                icon="fal fa-user-circle"
              />
            </AnterosFormGroup>
            <AnterosFormGroup row={false}>
              <AnterosLabel
                caption="Senha"
                small={2}
                style={{
                  color: '#464D69'
                }}
              />
              <AnterosPassword
                small={12}
                id="password"
                onChange={this.onChangePassword}
                value={this.state.password}
                disabled={authenticating}
                required
              />
            </AnterosFormGroup>
            <AnterosFormGroup row={false}>
              <AnterosAlert
                danger
                fill
                isOpen={!this.state.error ? false : true}
                autoCloseInterval={25000}
                onClose={this.onCloseAlert}
              >
                {this.state.error}
              </AnterosAlert>
            </AnterosFormGroup>
            <AnterosFormGroup row={false}>
              <AnterosLabel
                caption=""
                small={2}
                style={{
                  color: '#464D69'
                }}
              />
              <AnterosCheckbox
                small={12}
                id="chLogin"
                style={{
                  color: '#464D69'
                }}
                value="Lembre-me."
                checked={this.state.remindMe}
                valueChecked={true}
                onCheckboxChange={this.onChangeRemindMe}
                disabled={authenticating}
                valueUnchecked={false}
              />
            </AnterosFormGroup>
            <AnterosFormGroup row={false}>
              <div
                style={{
                  alignItems: 'center',
                  display: 'flex',
                  paddingLeft: '15px'
                }}
              >
                <AnterosButton
                  block
                  medium
                  style={botaoEntrarStyle}
                  disabled={authenticating}
                  caption={authenticating ? 'Aguarde...' : 'Entrar'}
                  onButtonClick={this.login}
                />
                <AnterosLabel
                  caption="Esqueceu sua senha?"
                  style={{
                    color: '#464D69'
                  }}
                />
              </div>
            </AnterosFormGroup>
            {/* <AnterosFormGroup row={false}>
              <div
                style={{
                  alignItems: 'center',
                  justifyContent: 'center',
                  display: 'flex',
                  paddingLeft: '15px'
                }}
              >
                <AnterosLabel
                  caption="Ou acesse com: "
                  style={{
                    color: '#6B798F'
                  }}
                />
                <AnterosButton
                  block
                  facebook
                  medium
                  style={botaoEntrarStyle}
                  disabled={authenticating}
                  caption="Facebook"
                />
                <AnterosButton
                  block
                  googlePlus
                  medium
                  style={botaoEntrarStyle}
                  disabled={authenticating}
                  caption="Google"
                />
                <AnterosButton
                  block
                  instagram
                  medium
                  style={botaoEntrarStyle}
                  disabled={authenticating}
                  caption="Instagram"
                />
              </div>
            </AnterosFormGroup> */}
          </AnterosForm>
        </div>
      </AnterosFullBackground>
    );
  }
}

const mapStateToProps = state => {
  return {
    user: state.authenticationReducer.user,
    authenticating: state.authenticationReducer.authenticating,
    authenticated: state.authenticationReducer.authenticated
  };
};

export default connect(
  mapStateToProps,
  null
)(AnterosLoginContainer2);

AnterosLoginContainer2.propTypes = {
  onLoginSuccess: PropTypes.func
};

AnterosLoginContainer2.defaultProps = {
  onLoginSuccess: f => f
};
